mapper.select:
select b.no, b.title, b.cdt, b.vw_cnt, b.like_cnt, m.no as writer_no, m.name as writer_name from pms_board b inner join pms_member m on m.no=b.writer
where b.title like concat('%', #{keyword},'%') or b.content like concat('%', #{keyword},'%') or m.name like concat('%', #{keyword},'%')
order by b.no desc limit #{offset}, #{length}

mapper.update:
update pms_board set
    title = #{title}, 
    content = #{content}
where no = #{no}

update pms_board set 
  vw_cnt = vw_cnt + 1 
where no = #{value}
